import React from 'react';
import { useCart } from '../context/CartContext';

const Cart = () => {
  const { getCartItems, removeFromCart, updateQuantity, getTotal } = useCart();
  const cartItems = getCartItems();

  const formatPrice = (price) => {
    return new Intl.NumberFormat('fa-IR').format(price) + ' تومان';
  };

  if (cartItems.length === 0) {
    return (
      <div className="bg-white p-4 rounded-lg shadow-md">
        <p className="text-center text-gray-600">سبد خرید شما خالی است</p>
      </div>
    );
  }

  return (
    <div className="bg-white p-4 rounded-lg shadow-md">
      <h2 className="text-xl font-bold mb-4">سبد خرید</h2>
      <div className="space-y-4">
        {cartItems.map(item => (
          <div key={item.productId} className="flex items-center justify-between border-b pb-4">
            <div className="flex items-center space-x-4">
              <img
                src={item.product.image_url}
                alt={item.product.name}
                className="w-16 h-16 object-cover rounded"
              />
              <div>
                <h3 className="font-semibold">{item.product.name}</h3>
                <p className="text-gray-600">{formatPrice(item.product.price)}</p>
              </div>
            </div>
            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-2">
                <button
                  onClick={() => updateQuantity(item.productId, Math.max(1, item.quantity - 1))}
                  className="bg-gray-200 px-2 py-1 rounded"
                >
                  -
                </button>
                <span>{item.quantity}</span>
                <button
                  onClick={() => updateQuantity(item.productId, item.quantity + 1)}
                  className="bg-gray-200 px-2 py-1 rounded"
                >
                  +
                </button>
              </div>
              <button
                onClick={() => removeFromCart(item.productId)}
                className="text-red-500 hover:text-red-700"
              >
                حذف
              </button>
            </div>
          </div>
        ))}
      </div>
      <div className="mt-4 pt-4 border-t">
        <div className="flex justify-between items-center">
          <span className="font-bold">جمع کل:</span>
          <span className="text-green-600 font-bold">{formatPrice(getTotal())}</span>
        </div>
        <button className="w-full mt-4 bg-green-500 text-white py-2 rounded hover:bg-green-600">
          پرداخت
        </button>
      </div>
    </div>
  );
};

export default Cart;
