import React, { useState } from 'react';
import { CartProvider } from './context/CartContext';
import { fakeProducts, fakeCategories } from './data/fakeData';
import ProductCard from './components/ProductCard';
import Cart from './components/Cart';

function App() {
  const [selectedCategory, setSelectedCategory] = useState('همه');
  const [searchQuery, setSearchQuery] = useState('');

  const filteredProducts = fakeProducts.filter(product => {
    const matchesCategory = selectedCategory === 'همه' || product.category === selectedCategory;
    const matchesSearch = product.name.includes(searchQuery) || 
                         product.description.includes(searchQuery);
    return matchesCategory && matchesSearch;
  });

  return (
    <CartProvider>
      <div className="min-h-screen bg-gray-100">
        <header className="bg-white shadow-md">
          <div className="container mx-auto px-4 py-4">
            <div className="flex justify-between items-center">
              <h1 className="text-2xl font-bold">فروشگاه آنلاین</h1>
              <div className="relative">
                <input
                  type="text"
                  placeholder="جستجوی محصولات..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="w-64 px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
            </div>
          </div>
        </header>

        <main className="container mx-auto px-4 py-8">
          <div className="flex gap-8">
            <div className="w-3/4">
              <div className="mb-6">
                <div className="flex space-x-4">
                  <button
                    onClick={() => setSelectedCategory('همه')}
                    className={`px-4 py-2 rounded ${
                      selectedCategory === 'همه'
                        ? 'bg-blue-500 text-white'
                        : 'bg-gray-200'
                    }`}
                  >
                    همه
                  </button>
                  {fakeCategories.map(category => (
                    <button
                      key={category.id}
                      onClick={() => setSelectedCategory(category.name)}
                      className={`px-4 py-2 rounded ${
                        selectedCategory === category.name
                          ? 'bg-blue-500 text-white'
                          : 'bg-gray-200'
                      }`}
                    >
                      {category.name}
                    </button>
                  ))}
                </div>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {filteredProducts.map(product => (
                  <ProductCard key={product.id} product={product} />
                ))}
              </div>
            </div>

            <div className="w-1/4">
              <Cart />
            </div>
          </div>
        </main>
      </div>
    </CartProvider>
  );
}

export default App;
